/**
 * Generate a complete SVG string from an icon definition
 */
export function generateIconSvg(iconName: string, size: string = '24', color: string = 'currentColor'): string {
  const icon = iconDefinitions[iconName];
  if (!icon) {
    console.warn(`Icon "${iconName}" not found`);
    return '';
  }

  const fill = icon.fill || 'none';
  const stroke = icon.stroke || color;
  const strokeWidth = icon.strokeWidth || '';
  const strokeLinecap = icon.strokeLinecap || '';
  const strokeLinejoin = icon.strokeLinejoin || '';

  let svgAttrs = `viewBox="${icon.viewBox}" width="${size}" height="${size}"`;
  // Always set fill to establish color context for nested currentColor
  if (fill === 'none') {
    svgAttrs += ` fill="none" color="${color}"`;
  } else {
    svgAttrs += ` fill="${fill === 'currentColor' ? color : fill}"`;
  }
  if (stroke) svgAttrs += ` stroke="${stroke === 'currentColor' ? color : stroke}"`;
  if (strokeWidth) svgAttrs += ` stroke-width="${strokeWidth}"`;
  if (strokeLinecap) svgAttrs += ` stroke-linecap="${strokeLinecap}"`;
  if (strokeLinejoin) svgAttrs += ` stroke-linejoin="${strokeLinejoin}"`;

  return `<svg xmlns="http://www.w3.org/2000/svg" ${svgAttrs}>${icon.content}</svg>`;
}

/**
 * Get all available icon names
 */
export function getAvailableIcons(): string[] {
  return Object.keys(iconDefinitions).sort();
}
